import React, { useCallback } from 'react';
import styled from 'styled-components';
import { isMobile, navBarHeight } from '../../styles/style';
import { Modal } from '../../utils/Modal';

const Container = styled.div`
  position: absolute;
  z-index: 10;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.3);
`;

const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Close = styled.img`
  position: absolute;
  top: 10px;
  right: 10px;
  width: 35px;
  height: 35px;
`;

const Content = styled.div`
  position: relative;
  background-color: blue
`;

const ContentImage = styled.img`
  width: ${(props) => props.width / 3}px;
  ${isMobile} {
    width: ${(props) => props.width}px;
  }
`;

const ProductId = styled.div`
  position: absolute;
  top: 5px;
  right: 5px;
  color: red;
  text-shadow: 1px 1px 10px white;
`;

export const ProductDetail = ({ product }) => {
  const handleClickClose = useCallback(() => {
    Modal.emitEvent();
  }, []);

  return (
    <Container>
      <Wrapper>
        <Close onClick={handleClickClose} src="https://user-images.githubusercontent.com/22593217/110236681-8b048900-7f7a-11eb-81ef-249448143231.png" alt="close" />
        <Content>
          <ContentImage src={product.image} alt={'Product'} width={window.innerWidth} />
        </Content>
      </Wrapper>

    </Container>
  );
};
