import { ArrowPrevious } from '../common/ArrowPrevious';

const Container = styled.div`
  display: flex;
  width: 100%;
  background-color: ${lightGrayColor};
  overflow: hidden;
  flex-direction: row;
  position: relative;
`;

const Content = styled.div`
  white-space: nowrap;
  overflow: hidden;
  margin: 10px 30px;
  height: 310px;
  display: flex;
  align-items: center;
`;

const ProductWrapper = styled.div`
  margin: 0 10px;
`;

