import PropTypes from 'prop-types';
import React from 'react';
import styled from 'styled-components';
import Product from '../../containers/main/Product';
import { lightGrayColor } from '../../styles/style';
import { ArrowNext } from '../common/ArrowNext';
import { ArrowPrevious } from '../common/ArrowPrevious';

const Container = styled.div`
  display: flex;
  width: 100%;
  background-color: ${lightGrayColor};
  overflow: hidden;
  flex-direction: row;
`;

const Content = styled.div`
  white-space: nowrap;
  overflow-x: scroll;
  margin: 10px auto;
  height: 310px;
  display: flex;
  align-items: center;
`;

const ArrowStyle = {
  position: 'absolute',
  width: '20px',
}

const ProductList = ({ products }) => (
  <Container>
    <ArrowPrevious style={ArrowStyle} />
    <Content>
      {
        products.map((product) => <Product key={product.id} product={product} />)
      }
    </Content>
    <ArrowNext style={ArrowStyle} />
  </Container>
);

ProductList.propTypes = {
  products: PropTypes.array.isRequired,
};

export default ProductList;
